		var msg = '';
		msg = 'Form : ' + f.name + '\n';
		msg += ' Table = ' + '<%=strAirCraftCalendarTable%>' + '\n\n'
		msg += ' DayNo = ' + iDayNo + '\n\n'
		// NOTE: FireFox Browser falls over at this line of code
		//
			values += f.elements('hdn_' + iDayNo + '_AM').value + ',';
