	private ImageDescriptor getImageDescriptor(String fullName) {

		String[] fullNamePathes = fullName.split("/");
		if (fullNamePathes.length > 1) {
			String fileName = fullNamePathes[fullNamePathes.length-1];
			String filePath = fullName.substring(0, fullName.length()-fileName.length());
			for (IImagePathExtension iconPath : iconPathes) {
				String fullPath = iconPath.getPath() + '/'; 
				if (fullPath.endsWith(filePath)) {
					fullPath = fullPath + fileName;
					URL url = iconPath.getContributingBundle().getResource(fullPath);
					if (url != null) {
						return ImageDescriptor.createFromURL(url);
					}
				}
			}
